package pt.ulisboa.tecnico.meic.sirs.group6.securesms.dataAccess;

import junit.framework.TestCase;

import org.junit.After;
import org.junit.Before;
import org.junit.Test;

import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;
import java.util.Date;

import javax.crypto.KeyGenerator;
import javax.crypto.SecretKey;
import javax.crypto.spec.SecretKeySpec;

/**
 * Created by lribeirogomes on 18/11/15.
 */
public class EncryptionTest extends TestCase {

    private String _password, _content;
    private long _startTime, _endTime;
    private EncryptionManager _manager;
    private SecretKey _key;

    @Before
    public void setUp() {
        _manager = EncryptionManager.getInstance();
        _content = "Hello World";
        _startTime = new Date().getTime();
        try {
            KeyGenerator keyGen = KeyGenerator.getInstance("AES");
            keyGen.init(128, new SecureRandom());
            _key = keyGen.generateKey();
        }catch (NoSuchAlgorithmException e){
            System.out.println("NoSuchAlgorithmException");
        }
    }

    @After
    public void tearDown() {
        _endTime = new Date().getTime();
        long difference = _endTime - _startTime;
        System.out.println("Elapsed milliseconds: " + difference);
    }

    @Test
    public void testEncryptionWithSmallPassword() throws Exception {
        _password = "password1";
        byte[] cipherText = _manager.encryptWithPassword(_content, _password);
        String plainText = _manager.decryptWithPassword(cipherText, _password);

        //assertEquals(_content, plainText);
    }

    @Test
    public void testEncryptionWithLongPassword() throws Exception {
        _password = "";
        byte[] cipherText = _manager.encryptWithPassword(_content, _password);
        String plainText = _manager.decryptWithPassword(cipherText, _password);

        //assertEquals(_content, plainText);
    }

    @Test
    public void testEncryptionWithSessionKey() throws Exception {
        byte[] cipherText = _manager.encryptWithSessionKey(_content, _key);
        String plainText = _manager.decryptWithSessionKey(cipherText, _key);

        //assertEquals(_content, plainText);
    }
}